import type { TestURLResponseError } from './testURLResponseError';
import type { TestURLResponseRecords } from './testURLResponseRecords';

export type TestURLResponse = {
  /**
   * The datetime when the test started.
   */
  startDate: string;
  /**
   * The datetime when the test ended.
   */
  endDate: string;
  /**
   * The list of logs generated by `console.log()` in the recordExtractors.
   */
  logs: string[][];
  /**
   * The list of records generated for the given URL.
   */
  records: TestURLResponseRecords[];
  /**
   * The list of links found on the page, that match the configuration (and would be processed).
   */
  links: string[];
  /**
   * The External Data associated to the tested URL. External data are refreshed at each full reindex.
   */
  externalData?: Record<string, any>;
  error?: TestURLResponseError;
};
